//index.js
//获取应用实例
var app = getApp();

var imgIndex = 0,
    playing = false,
    audioUrls = [
        'http://cdn.calm.com/scenes/scene-Qqkzy9k7Eo.m4a?v=1418162240715',
        'https://dn-working-noise.qbox.me/noise/min/Ocean.mp3',
        'https://dn-working-noise.qbox.me/noise/min/Forest.mp3',
        'https://dn-working-noise.qbox.me/noise/min/Rain.mp3',
        'https://dn-working-noise.qbox.me/noise/min/CoffeeShop.mp3',
    ];

Page({
    data: {
        imgUrls: [
            'data:image/jpeg;base64,',
            'data:image/jpeg;base64,',
            'data:image/jpeg;base64,',
            'data:image/jpeg;base64,',
            'data:image/jpeg;base64,'
        ],
        duration: 1000,
        src: audioUrls[0],
        userInfo: {},
        noteStyle: [],
    },

    onLoad: function () {
        this.setData({
            userInfo: app.globalData.userInfo
        })

    },

    onReady: function (e) {
        // 使用 wx.createAudioContext 获取 audio 上下文 context
        this.audioCtx = wx.createAudioContext('myAudio')
    },

    intervalChange: function(e) {
        imgIndex = e.detail.current;
        
        this.audioCtx.pause();
        playing = false;

        this.setData({
            src: audioUrls[imgIndex],
        });
    },

    tapImgItem: function () {
        if ( playing ) {
            this.audioCtx.pause();
            playing = false;
        }
    },

    changeIndicatorDots: function(e) {
        if ( playing ) {
            return;
        }

        this.setData({
            src: audioUrls[imgIndex],
        });

        this.audioCtx.play();
        playing = true;
    },

    bindViewTap: function () {
      wx.navigateTo({
        url: '../logs/logs'
      })
    },

    saveNote: function () {
        this.setData({
            noteStyle: 'display:none'
        })
    },

})
